import requestsimport xbmcguiimport xbmcimport xbmcaddonfrom bs4 import BeautifulSoupdialog = xbmcgui.Dialog()Headers = {'User-Agent' : 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/60.0.3112.113 Safari/537.36' }GoodFiles = ['.mp4', '.mkv']class Scraper:	def __init__(self):		self.Base = 'https://iwaatch.com'		self.Search = ('/movie/%s')		self.links = []	def Index(self,type,term,year,imdb,torrents):		if type == 'MOVIE':			try:				filmname = term.title()				term = term.replace(' ','_').replace(':','%3A')				link = requests.get(self.Base+self.Search % term,headers=Headers).content				soup = BeautifulSoup(link, 'html5lib')				data = soup.find('span', class_={'download-q'})				for links in data.find_all('a'):					source = links['href']					qual = links.text					if '360' in qual:						title = ('IWAATCH ( DIRECT ) | SD | %s' % filmname)						quality = '4'					elif '480' in qual:						title = ('IWAATCH ( DIRECT ) | SD | %s' % filmname)						quality = '4'					elif '720' in qual:						title = ('IWAATCH ( DIRECT ) | HD | %s' % filmname)						quality = '3'					elif '1080' in qual:						title = ('IWAATCH ( DIRECT ) | FHD | %s' % filmname)						quality = '2'					else:						title = ('IWAATCH ( DIRECT )| Unkown | %s' % filmname)						quality = '4'					self.links.append({'title': title, 'url': source, 'quality': quality, 'Debrid' : False, 'Direct' : True})				return self.links			except Exception as c:				xbmc.log("SCRAPER ERROR IWaatch  ::: %s" %c , level=xbmc.LOGNOTICE)		else: pass