import requestsimport reimport xbmcimport xbmcguiimport timefrom random import randintdialog = xbmcgui.Dialog()from cloudscraper2 import CloudScraperCF = CloudScraper.create_scraper()ua = {"User-Agent":"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/72.0.3626.121 Safari/537.36",      "Referer" : "https://www.limetorrents.info/"}class Scraper:	def __init__(self):		self.Base = 'https://www.limetorrents.info/'		self.Search = ('search/movies/%s/seeds/1/')		self.SearchTv = ('search/tv/%s/seeds/1/')		self.links = []	def Index(self,type, term,year,imdb,torrents):		if type == 'TV':			MovieName = term			term = term.replace(' ','-')			link = CF.get(self.Base+self.SearchTv %term, headers=ua).content			match = re.findall('Torrent Name</span>(.*?)<div id="rightbar">',link,flags=re.DOTALL)[0]			pattern = r'''href=['"].*?torrent/(.*?).torrent.*?title=(.*?)['"]'''			getlinks = re.findall(pattern,match)			if not getlinks: return False			for link,name in getlinks:					link2 = ('magnet:?xt=urn:btih:%s&dn=%s' % (link,name))					if 'uhd' in name.lower(): sort = '5' ; qual = '4K UHD'					elif '2160' in name.lower(): sort = '5'; qual = '4K UHD'					elif '1080' in name.lower(): sort = '6'; qual = 'FHD'					elif '720' in name.lower(): sort = '7'; qual = 'HD'					elif 'hdtv' in name.lower(): sort = '7'; qual = 'HD'					else : sort = '8'; qual = 'SD'					title = 'LimeTorrents ( Debrid ) | ' + qual + ' | ' +MovieName					self.links.append({'title': title, 'url': link2, 'quality' : sort, 'Debrid' : True, 'Direct' : False})			return self.links		else:			MovieName = term			term = term.replace(' ','+')			link = CF.get(self.Base+self.Search %term, headers=ua).content			match = re.findall('Torrent Name</span>(.*?)<div id="rightbar">',link,flags=re.DOTALL)[0]			pattern = r'''href=['"].*?torrent/(.*?).torrent.*?title=(.*?)['"]'''			getlinks = re.findall(pattern,match)			if not getlinks: return False			for link,name in getlinks:				if year.strip() in name:					link2 = ('magnet:?xt=urn:btih:%s&dn=%s' % (link,name))					if 'uhd' in name.lower(): sort = '5' ; qual = '4K UHD'					elif '2160' in name.lower(): sort = '5'; qual = '4K UHD'					elif '1080' in name.lower(): sort = '6'; qual = 'FHD'					elif '720' in name.lower(): sort = '7'; qual = 'HD'					elif 'hdtv' in name.lower(): sort = '7'; qual = 'HD'					else : sort = '8'; qual = 'SD'					title = '[COLOR yellow]LimeTorrents ( Debrid ) | [/COLOR]' + qual + ' | ' +MovieName					self.links.append({'title': title, 'url': link2, 'quality' : sort, 'Debrid' : True, 'Direct' : False})			return self.links