import requestsimport reimport xbmcimport xbmcguifrom bs4 import BeautifulSoupHeaders = {'User-Agent' : 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/60.0.3112.113 Safari/537.36' }dialog = xbmcgui.Dialog()class Scraper:	def __init__(self):		self.Base = 'https://www.skytorrents.lol/?query=%s'		self.Search = ('?query=')		self.links = []	def Index(self,type, term,year,imdb,torrents):		if type == 'TV':			MovieName = term			SE = re.findall('''(s[0-9]+e[0-9]+)''',term,flags=re.I)[0]			term = term.replace(' ','+')			link = requests.get(self.Base %term, headers=Headers).content			soup = BeautifulSoup(link,'html.parser')			data = soup.find_all("tr",  class_={'result'})			for i in data:				try:					title = i.img['alt']					if SE.lower() in title.lower():						magnet = i.select_one("a[href*=magnet]")['href']						if 'uhd' in title.lower(): sort = '5' ; qual = '4K UHD'						elif '2160' in title.lower(): sort = '5'; qual = '4K UHD'						elif '1080' in title.lower(): sort = '6'; qual = '1080'						elif '720' in title.lower(): sort = '7'; qual = '720'						elif 'hdtv' in title.lower(): sort = '7'; qual = '720'						else : sort = '8'; qual = 'SD'						name = 'SkyTorrents ( Real Debrid ) | ' + qual + ' | ' +MovieName						self.links.append({'title': name, 'url': magnet, 'quality' : sort})				except: pass			return self.links		else:			MovieName = term			year = year.strip()			term = term.replace(' ','+')			term = term+'+'+year			link = requests.get(self.Base %term, headers=Headers).content			soup = BeautifulSoup(link,'html.parser')			data = soup.find_all("tr",  class_={'result'})			for i in data:				try:					title = i.img['alt']					if year in title.lower():						magnet = i.select_one("a[href*=magnet]")['href']						if 'uhd' in title.lower(): sort = '5' ; qual = '4K UHD'						elif '2160' in title.lower(): sort = '5'; qual = '4K UHD'						elif '1080' in title.lower(): sort = '6'; qual = '1080'						elif '720' in title.lower(): sort = '7'; qual = '720'						elif 'hdtv' in title.lower(): sort = '7'; qual = '720'						else : sort = '8'; qual = 'SD'						name = 'SkyTorrents ( Real Debrid ) | ' + qual + ' | ' +MovieName						self.links.append({'title': name, 'url': magnet, 'quality' : sort})				except: pass			return self.links